                    <div className="col-md-5 ">

                        <h3>{room.name}</h3>
                        <img src={room.imageurls[0]} className='bigimg' />
                    </div>
                    <div className="col-md-5" >
                        <div style={{ textAlign: 'right' }}>
                            <h5>
                                Booking Details
                            </h5>
                            <hr />
                            <b>
                                <p>Name : </p>
                                <p>From Date : {fromdate} </p>
                                <p>To Date : {todate}</p>
                                <p>Max count : {room.maxcount} </p>
                            </b>

                        </div>
                        <div style={{ textAlign: 'right' }}>
                            <b>
                                <h5>Amount</h5>
