import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from "axios";

function Bookingscreen() {
    const [loading, setloading] = useState(true); // Initialized to true, assuming loading starts immediately
    const [error, seterror] = useState(false); // Initialized to false
    const [room, setroom] = useState(null); // Initialized to null for clarity

    // Extract `roomid` from the URL parameters
    const { roomid, fromdate, todate } = useParams();

    useEffect(() => {
        // Define an async function inside the useEffect
        const fetchRoomDetails = async () => {
            try {
                const response = await axios.post('http://localhost:5000/api/rooms/getroombyid', {
                    roomid: roomid // Use `roomid` obtained from `useParams`
                });

                setroom(response.data);
            } catch (error) {
                console.error(error);
                seterror(true);
            } finally {
                setloading(false);
            }
        };

        // Call the async function
        fetchRoomDetails();
    }, [roomid]); // Add `roomid` as a dependency to re-fetch if it changes

    if (loading) {
        return <h1>Loading...</h1>;
    }

    if (error) {
        return <h1>Error fetching room details.</h1>;
    }

    return (
        <div>
            {loading ? (<h1>Loading...</h1>) : error ? (<h1>Error...</h1>) : (<div>

                <div className="row justify-content-center mt-5">
                    <div className="col-md-5 ">

                        <h3>{room.name}</h3>
                        <img src={room.imageurls[0]} className='bigimg' />
                    </div>
                    <div className="col-md-5" >
                        <div style={{textAlign:'right'}}>
                            <h5>
                                Booking Details
                            </h5>
                            <hr />
                            <b>
                                <p>Name : </p>
                                <p>From Date : {fromdate} </p>
                                <p>To Date : {todate}</p>
                                <p>Max count :{room.maxcount} </p>
                            </b>

                        </div>
                        <div style={{textAlign:'right'}}>
                        <b>
                        <h5>Amount</h5>
                        <hr />
                        
                            <p>Total days : {todate - fromdate}</p>
                            <p>Rent per Day : {room.rentperday} </p>
                            <p>Total amount : </p>
                        </b>
                        </div>
                        <div style={{float:'right'}}>
                        <button className='btn btn-primary'>Pay Now</button>
                    </div>
                    </div>
                    

                </div>

            </div>)};
        </div>
    )
}

export default Bookingscreen;
